mapper.select:
SELECT m.id, m.title, m.content, m.sender_type, CASE WHEN m.sender_type = 'system' THEN '系统通知' WHEN u.username IS NOT NULL THEN u.username ELSE '管理员' END as sender_name, m.is_read, m.created_at, m.read_at FROM sys_message m LEFT JOIN sys_user u ON m.sender_id = u.id WHERE m.recipient_id = #{recipientId}
AND m.is_read = false
ORDER BY m.created_at DESC
